import React, { useState } from "react";
import styles from "./Menu.module.css";

const Menu = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className={styles.menuContainer}>
      <details open={isOpen}>
        <summary onClick={toggleMenu} className={styles.summary}>
          {isOpen ? "X" : "III"}
        </summary>
        <nav className={`${styles.menu} ${isOpen ? styles.menuOpen : ""}`}>
          <a href="#link">Home</a>
          <a href="#link">Work</a>
          <a href="#link">Links</a>
          <a href="#link">Contact</a>
          <a href="#link">About</a>
        </nav>
      </details>
    </div>
  );
};

export default Menu;
