import React from "react";
import styles from "./Api.module.css";

const Api = () => {
  return (
    <section className={styles.section}>
      <div className={styles.container}>
        <h1 className={styles.title}>The science behind BreatheSafetyIndex</h1>
        <div className={styles.textImageWrapper}>
          <p className={styles.aboutText}>
            BreatheSafetyIndex serves:oWeather ServicesoSmart City
            PlatformsoPublic Health ApplicationsoSports Event OrganizersEach of
            our clients benefits from high-frequency data calls and risk
            assessments tailored to their specific needs, helping them create
            safer, healthier environments.
          </p>
          <div className={styles.divider}></div>
          <p className={styles.aboutText}>
            Join Us on our Mission to identify and measure health threats.With
            air pollution impacting global health at an unprecedented scale, the
            need for data-driven, accessible tools is greater than ever. Partner
            with BreatheSafetyIndex to make impactful changes, enhance user
            engagement, and contribute to a healthier future.
          </p>
        </div>
      </div>
    </section>
  );
};

export default Api;
