import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import styles from "./Menu.module.css";

const Menu = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <nav className={styles.navbar}>
      <button className={styles.logoButton}>
        <span>BreatheSafetyIndex logo</span>
      </button>
      <button className={styles.tryNowButton}>Try now</button>
      <div className={styles.menuContainer}>
        <button className={styles.menuButton} onClick={toggleMenu}>
          Menu
        </button>
        {isMenuOpen && (
          <ul className={styles.dropdownMenu}>
            <li>
              <NavLink to="/home">Home</NavLink>
            </li>
            <li>
              <NavLink to="/benefits">Key benefits</NavLink>
            </li>
            <li>
              <NavLink to="/impact">Air pollution impact</NavLink>
            </li>
            <li>
              <NavLink to="/approach">Assessment approach</NavLink>
            </li>
            <li>
              <NavLink to="/nasa">NASA/TEMPO program</NavLink>
            </li>
            <li>
              <NavLink to="/api">API capabilities</NavLink>
            </li>
            <li>
              <NavLink to="/customers">For customers</NavLink>
            </li>
            <li>
              <NavLink to="/contacts">Contacts</NavLink>
            </li>
          </ul>
        )}
      </div>
    </nav>
  );
};

export default Menu;
